import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  const [showRegister, setShowRegister] = useState(false);
  const [regUsername, setRegUsername] = useState('');
  const [regPassword, setRegPassword] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    if (!regUsername || !regPassword) {
      alert('請填寫帳號與密碼');
      return;
    }

    // 取得現有註冊帳號列表
    const users = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    
    // 檢查帳號是否已存在
    if (users.some(u => u.username === regUsername)) {
      alert('帳號已存在');
      return;
    }

    // 加入新帳號
    users.push({ username: regUsername, password: regPassword });
    localStorage.setItem('registeredUsers', JSON.stringify(users));

    alert('註冊成功！請登入');
    setShowRegister(false);
    setRegUsername('');
    setRegPassword('');
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>AI 履歷健診平台</h1>
      <p>請先登入以使用平台功能</p>
      <button onClick={() => navigate('/login')} style={{ marginRight: '10px' }}>登入</button>
      <button onClick={() => setShowRegister(true)}>註冊</button>

      {showRegister && (
        <div style={{
          position: 'fixed', top: 0, left: 0,
          width: '100%', height: '100%',
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{ background: 'white', padding: '20px', borderRadius: '8px', minWidth: '300px' }}>
            <h2>註冊帳號</h2>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                placeholder="Username"
                value={regUsername}
                onChange={e => setRegUsername(e.target.value)}
                style={{ display: 'block', marginBottom: '10px', width: '100%' }}
              />
              <input
                type="password"
                placeholder="Password"
                value={regPassword}
                onChange={e => setRegPassword(e.target.value)}
                style={{ display: 'block', marginBottom: '10px', width: '100%' }}
              />
              <button type="submit" style={{ marginRight: '10px' }}>註冊</button>
              <button type="button" onClick={() => setShowRegister(false)}>取消</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
