import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import FileUpload from './components/FileUpload';
import ResumePreview from './components/ResumePreview';
import DiagnosisResult from './components/DiagnosisResult';
import bgImg from './components/background.jpg';

function Dashboard() {
  const [resumeText, setResumeText] = useState('');
  const [result, setResult] = useState(null);
  const navigate = useNavigate();

  // 檢查登入狀態
  useEffect(() => {
    const loggedIn = localStorage.getItem('loggedIn');
    if (!loggedIn) {
      navigate('/login');
    }
  }, [navigate]);

  // 執行履歷健診
  const handleDiagnosis = () => {
    if (!resumeText) {
      alert('請先上傳履歷！');
      return;
    }
    const score = Math.floor(Math.random() * 41) + 60; // 60-100
    const suggestions = [
      '增加更多專案經驗',
      '補充技能關鍵字',
      '加強自我介紹',
      '刪減過多的描述，突出重點'
    ];
    setResult({
      score,
      suggestions: suggestions.sort(() => 0.5 - Math.random()).slice(0, 2)
    });
  };

  // 登出
  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    navigate('/');
  };

  // 容器樣式
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    minHeight: '100vh',
    padding: '20px',
    backgroundImage: 'url(${bgImg})', // 使用 public 資料夾背景
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    textShadow: '1px 1px 2px black'
  };

  const buttonStyle = {
    padding: '10px 20px',
    fontSize: '1em',
    margin: '10px',
    cursor: 'pointer'
  };

  return (
    <div style={containerStyle}>
      <h1>AI 履歷健診平台</h1>
      <button style={buttonStyle} onClick={handleLogout}>登出</button>

      <FileUpload setResumeText={setResumeText} />
      <ResumePreview text={resumeText} />
      <button style={buttonStyle} onClick={handleDiagnosis}>執行健診</button>

      {result && <DiagnosisResult result={result} />}
    </div>
  );
}

export default Dashboard;
