import React from 'react';

function Dashboard() {
  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    window.location.href = '/';
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundImage: 'url("/background.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    padding: '0 10px'
  };

  const boxStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.85)',
    padding: '40px',
    borderRadius: '12px',
    width: '100%',
    maxWidth: '500px',
    textAlign: 'center',
    boxSizing: 'border-box'
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    fontSize: '1em',
    cursor: 'pointer',
    marginTop: '10px'
  };

  return (
    <div style={containerStyle}>
      <div style={boxStyle}>
        <h1>AI 履歷健診平台</h1>
        <p>歡迎使用平台！</p>
        <button style={buttonStyle} onClick={handleLogout}>登出</button>
      </div>
    </div>
  );
}

export default Dashboard;
